targets.expr:
(100*(
  sum by(pod,container) (irate(container_cpu_usage_seconds_total {node=~"$Node", container!="", pod!~".*prepull.*", namespace=~"$namespace", pod=~"$pod", container=~"$container", job="kubelet", container!="POD", image!=""}[4m])) 
/ sum by(pod,container) (      kube_pod_container_resource_limits{node=~"$Node", container!="", pod!~".*prepull.*", namespace=~"$namespace", pod=~"$pod", container=~"$container", job="kube-state-metrics", resource="cpu"  }    )
))
# {{container}}

# graph1: total-available ~= memrss            ... ((total-available)+dirtypages) = memrss+dirtypages
# graph2: total-free      ~= memrss+cached+buffers  ... ((total-free)+dirtypages) = memrss+dirtypages+cached+buffers
#( (node_memory_MemTotal_bytes{nodename=~"$Node"}-node_memory_MemAvailable_bytes{nodename=~"$Node"}) + node_memory_Dirty_bytes{nodename=~"$Node"}) / node_memory_MemTotal_bytes{nodename=~"$Node"}
sum by(nodename)(
    ( (node_memory_MemTotal_bytes{nodename=~"$Node"}-node_memory_MemAvailable_bytes{nodename=~"$Node", nodename=~".*$NodeClass.*"}) + node_memory_Dirty_bytes{nodename=~"$Node", nodename=~".*$NodeClass.*"}) / node_memory_MemTotal_bytes{nodename=~"$Node", nodename=~".*$NodeClass.*"}
)

( instance:node_memory_utilisation:ratio{job="node-exporter", nodename=~"$Node"}
  / ignoring (instance) group_left count without (instance) (instance:node_memory_utilisation:ratio{job="node-exporter", nodename=~"$Node"})
) + ( node_memory_Dirty_bytes{nodename=~"$Node"} / node_memory_MemAvailable_bytes{nodename=~"$Node"} )

( sum without (device) (
    max without () (
      node_filesystem_size_bytes{job="node-exporter", instance=~"$instance", fstype!="tmpfs", fstype!="fuse.lxcfs", nodename=~".*$NodeClass.*"} 
    - node_filesystem_free_bytes{job="node-exporter", instance=~"$instance", fstype!="tmpfs", fstype!="fuse.lxcfs", nodename=~".*$NodeClass.*"}
    )
  ) 
/ ignoring (instance) group_left
  sum without (instance, device) (
    max without () (
      node_filesystem_size_bytes{job="node-exporter", instance=~"$instance", fstype!="tmpfs", fstype!="fuse.lxcfs", nodename=~".*$NodeClass.*"}
    )
  )
)

# PVC: {{persistentvolumeclaim}}
# pvc({{persistentvolumeclaim}}) - ns({{namespace}})
( kubelet_volume_stats_capacity_bytes{persistentvolumeclaim=~"$pvc", namespace=~"$namespace", node=~".*$NodeClass.*"} 
- kubelet_volume_stats_available_bytes{persistentvolumeclaim=~"$pvc", namespace=~"$namespace", node=~".*$NodeClass.*"})
/ kubelet_volume_stats_capacity_bytes{persistentvolumeclaim=~"$pvc", namespace=~"$namespace", node=~".*$NodeClass.*"}

# available - free = cached... fixing by changing this "total mem use (rss+cache)" to just MemAvailable
# graph1: total-available ~= memrss            ... ((total-available)+dirtypages) = memrss+dirtypages
# graph2: total-free      ~= memrss+cached+buffers
# ( (node_memory_MemTotal_bytes{nodename=~"$Node"}-node_memory_MemFree_bytes{nodename=~"$Node"}) ) / node_memory_MemTotal_bytes{nodename=~"$Node"}
sum by(nodename)(
    ( (node_memory_MemTotal_bytes{nodename=~"$Node", nodename=~".*$NodeClass.*"}-node_memory_MemFree_bytes{nodename=~"$Node", nodename=~".*$NodeClass.*"}) ) / node_memory_MemTotal_bytes{nodename=~"$Node", nodename=~".*$NodeClass.*"}
)

(   node_memory_MemAvailable_bytes{nodename=~"$Node"} 
  - node_memory_MemFree_bytes{nodename=~"$Node"} 
  - node_memory_Cached_bytes{nodename=~"$Node"}
  - node_memory_Buffers_bytes{nodename=~"$Node"}
  #- node_memory_Dirty_bytes{nodename=~"$Node"}
  #? node_memory_Active_bytes{nodename=~"$Node"}
) / node_memory_MemTotal_bytes{nodename=~"$Node"}
# (available-free)/total                       =  2.600% to 67.90% ...
# (available-free-cached)/total                = -0.913% to  3.37% ... => available ~= free +cached             (this is the % of memory "available" for use by processes )
# (available-free-cached-buffers)/total        = -0.977% to  2.23% ... => available ~= free +cached +buffers    I think (total-free) = memRss      what is node_memory_Active_bytes ?
# (available-free-cached-dirty)/total          = -2.500% to  3.36% ... (dirty pages are part of cache that are not yet on disk, so I think this line doesnt make sense)
# graph1: total-available ~= memrss    ... ((total-available)+dirtypages) = memrss+dirtypages
# graph2: total-free      ~= memrss+cached  ... ((total-free)+dirtypages) = memrss+dirtypages+cached+buffers+etc

(   sum by(nodename) ( irate( node_cpu_seconds_total{nodename=~"$Node", nodename=~".*$NodeClass.*",mode="user"}[1m] ) ) 
  + sum by(nodename) ( irate( node_cpu_seconds_total{nodename=~"$Node", nodename=~".*$NodeClass.*", mode="nice"}[1m] ) )
) / sum(irate(node_cpu_seconds_total[1m])) by(nodename)

