sum by (nodeprefix, resource)(
  label_replace ( 
    max by(pod, node, resource)( 
      kube_pod_container_resource_requests {node=~"$Node", node=~".*$NodeClass.*"} or
      kube_pod_init_container_resource_requests {node=~"$Node", node=~".*$NodeClass.*"}
    )
  ,"nodeprefix", "$1", "node", "(aks-[^-]+).+")
)
/ sum by (nodeprefix, resource)(
    label_replace ( 
      kube_node_status_allocatable {node=~"$Node", node=~".*$NodeClass.*"}
      ,"nodeprefix", "$1", "node", "(aks-[^-]+).+"
    )
  )

